import React, { Component } from 'react'
import {connect} from 'react-redux'
import ProfileImg from '../../profile.png'


class Profile extends Component {
  constructor(props){
    super(props);
    this.state={
        selected: {...this.props.profile[0]},
    }
  }
  setSelected = e =>{
    e.preventDefault();
    const selected = this.props.profile.find(bio => bio.title === e.target.innerText);
    this.setState({selected: {...selected}})
  }
 expandItem = e =>{
  e.target.classList.add('w-1/2')
  e.target.classList.remove('w-1/3')

 }
  render() {
    return (
      <div className="flex flex-wrap w-3/5 mx-auto my-24">
        <div onClick={e=>this.expandItem(e)} className="w-1/3 text-center h-64 flex justify-center items-center border border red hover:cursor-pointer">
          <h3>Developer</h3>
        </div>
      <div className="w-1/3 text-center h-64 flex justify-center items-center border border red">
          <h3>Content Creator</h3>
        </div>
      <div className="w-1/3 text-center h-64 flex justify-center items-center border border red">
          <h3>Person</h3>
        </div>
      </div>

    )
  }
}

const mapStateToProps = (state) => ({
  profile: state.profile
})

const mapDispatchToProps = {

}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(Profile)
